import React, { Component } from "react";
import { Form, Field } from "@progress/kendo-react-form";
import { Input } from "@progress/kendo-react-inputs";
import { DropDownList } from "@progress/kendo-react-dropdowns";


const CustomInput = ({ fieldType, value, visited, touched, valid,
  onChange, validationMessage }) => {
  return (
    <div>
      <Input
        type={fieldType}
        className={invalid ? "invalid" : ""}
        value={value}
        onChange={onChange} />
      { invalid && 
        (<div className="required">{validationMessage}</div>) }
    </div>
  );
};

const CustomDropDown = ({ options, fieldType, value, visited, touched, valid,
  onChange, validationMessage}) => {
  return (
    <div>
      <DropDownList
        data={options}
        type={fieldType}
        className={invalid ? "invalid" : ""}
        value={value}
        onChange={onChange} />
      { invalid && 
        (<div className="required">{validationMessage}</div>) }
    </div>
  )
}


const ValidationMessage = ({ valid, visited, validationMessage }) => {
  return (
    <>
    { !valid && visited &&
        (<div className="required">{validationMessage}</div>)}
    </>
  );
}

const emailValidator = (value) => (
  new RegExp(/\S+@\S+\.\S+/).test(value) ? "" : "Please enter a valid email."
);
const requiredValidator = (value) => {
  return value ? "" : "This field is required";
}

export default function App() {
  const handleSubmit = (data) => {
    console.log(`
    Category: ${data.category}
    Name: ${data.name}
    Email: ${data.email}
    Address: ${data.address}
    Message: ${data.message}
    `);

    event.preventDefault();
  }

  return (
    <Form
      onSubmit={handleSubmit}
      render={(formRenderProps) => (
        <form onSubmit={formRenderProps.onSubmit}>
          <h1>Post a listing</h1>

          <Field 
            label="Category:"
            name="category"
            component={DropDown}
            options={categories}
            validator={requiredValidator} />

          <Field
            label="Name:"
            name="name"
            fieldType="text"
            component={Input}
            validator={requiredValidator} />

          <Field
            label="Email:"
            name="email"
            fieldType="email"
            component={Input}
            validator={[requiredValidator, emailValidator]} />

          <Field
            label="Address:"
            name="address"
            fieldType="address"
            component={Input}
            validator={requiredValidator} />

          <Field
            label="Message:"
            name="message"
            fieldType="text"
            component={Input}
            validator={requiredValidator} />

          <button> Submit </button>
        </form>
      )}>
    </Form>
  );
}
