import React from 'react';
import './PostListing.scss';
import Button from './Button';
import { useState } from 'react';

function PostListing(props) {
    const [formData, setFormData] = useState({
      category : ""
    });

  function submit () {
    const grower = {...state.grower}
    setState({...state, grower})
    console.log(grower);
  }

  const handleCategory = (event) => {
    formData[event.target.name] = event.target.value;

  }
  return (
    <body class="body">
      <div class="background">
        <div class="blur"></div>
      </div>
      <div class="modal" id="modal">
        <div class="content">
          <form class='form' autoComplete="off" onSubmit={event => event.preventDefault()}>
            <label for="category" onChange = {handleCategory}>I am a</label>
            <select class='label' id="category" name="category">
              <option value="landholder">Landholder</option>
              <option value="grower">Grower</option>
            </select>
            <input class='input' placeholder='Name' />
            <input class='input' placeholder='Email' />
            <input class='input' placeholder='Address' />
            <textarea class='input' placeholder='Message' />
          </form>
        </div>
        <div class="actions">
          <Button class="toggle-button" onClick={props.close}>Close</Button>
          <Button class="toggle-button" onClick={submit}>Submit</Button>
        </div>
      </div>
    </body>
  );

}


export default PostListing;
